import { Fragment, useCallback, useEffect, useMemo, useState } from "react"
import { InputSelect } from "./components/InputSelect"
import { Instructions } from "./components/Instructions"
import { Transactions } from "./components/Transactions"
import { useEmployees } from "./hooks/useEmployees"
import { usePaginatedTransactions } from "./hooks/usePaginatedTransactions"
import { useTransactionsByEmployee } from "./hooks/useTransactionsByEmployee"
import { EMPTY_EMPLOYEE } from "./utils/constants"
import { Employee, SetTransactionApprovalParams } from "./utils/types"

export function App() {
  const { data: employees, ...employeeUtils } = useEmployees()
  const { data: paginatedTransactions, ...paginatedTransactionsUtils } = usePaginatedTransactions()
  const { data: transactionsByEmployee, ...transactionsByEmployeeUtils } = useTransactionsByEmployee()
  // State variables to manage loading status for employees and transactions
  const [isLoadingEmployees, setIsLoadingEmployees] = useState(false);
  const [isLoadingTransactions, setIsLoadingTransactions] = useState(false);
  const [transactionApprovals, setTransactionApprovals] = useState<{ [key: string]: boolean }>({});

  const setTransactionApproval = useCallback(
    async ({ transactionId, newValue }) => {
      // Update the mock data
      setTransactionApproval({ transactionId, value: newValue });

      // Update local state
      setTransactionApprovals((prevState) => ({ ...prevState, [transactionId]: newValue }));
    },
    []  // No dependencies here as setTransactionApproval is a simple function
  );


  const transactions = useMemo(
    () => paginatedTransactions?.data ?? transactionsByEmployee ?? null,
    [paginatedTransactions, transactionsByEmployee]
  )

  const loadAllTransactions = useCallback(async () => {
    // Set loading status for transactions
    setIsLoadingTransactions(true);
    transactionsByEmployeeUtils.invalidateData()
    await employeeUtils.fetchAll();
    // Set loading status for employees to false immediately after fetching
    setIsLoadingEmployees(false); 
    await paginatedTransactionsUtils.fetchAll();
    // Set loading status for transactions to false after fetching
    setIsLoadingTransactions(false);
  }, [employeeUtils, paginatedTransactionsUtils, transactionsByEmployeeUtils]);


  const loadTransactionsByEmployee = useCallback(
    async (employeeId: string) => {
      if (transactionsByEmployee === null || transactionsByEmployee.length === 0) {
        paginatedTransactionsUtils.invalidateData();
      }
      await transactionsByEmployeeUtils.fetchById(employeeId);
    },
    [paginatedTransactionsUtils, transactionsByEmployee, transactionsByEmployeeUtils]
  );



  useEffect(() => {
    if (employees === null && !employeeUtils.loading) {
      // Set loading status for employees before fetching
      setIsLoadingEmployees(true);
      loadAllTransactions();
    }
  }, [employeeUtils.loading, employees, loadAllTransactions]);


  return (
    <Fragment>
      <main className="MainContainer">
        <Instructions />

        <hr className="RampBreak--l" />

        <InputSelect<Employee>
          // Use isLoadingEmployees to control the loading state of InputSelect
          isLoading={isLoadingEmployees}
          defaultValue={EMPTY_EMPLOYEE}
          items={employees === null ? [] : [EMPTY_EMPLOYEE, ...employees]}
          label="Filter by employee"
          loadingLabel="Loading employees"
          parseItem={(item) => ({
            value: item.id,
            label: `${item.firstName} ${item.lastName}`,
          })}
          onChange={async (newValue) => {
            if (newValue === null) {
              return;
            }
            const hasValidId = newValue.id !== "";
            const loadTransactions = hasValidId ? loadTransactionsByEmployee : loadAllTransactions;
            // Load transactions based on the presence of a valid ID
            await loadTransactions(newValue.id);
          }}

        />

        <div className="RampBreak--l" />

        <div className="RampGrid">
        <Transactions transactions={transactions} setTransactionApproval={setTransactionApproval} />
          {transactions !== null && paginatedTransactions?.nextPage !== null &&(
            <button
              className="RampButton"
              disabled={
                paginatedTransactionsUtils.loading || paginatedTransactions?.nextPage == null
                  ? true
                  : false
              }
              onClick={async () => {
                await loadAllTransactions()
              }}
            >
              {/*control the loading state of the button */}
              {paginatedTransactionsUtils.loading? "Loading..." : "View More"}
            </button>
          )}
        </div>
      </main>
    </Fragment>
  )
}
